mapper.select:
SELECT COUNT(*)
FROM REVIEW_REPLY
WHERE REVIEW_NO = #{searchVO.review_no}

SELECT REVIEW_REPLY_NO, REVIEW_NO, REVIEW_REPLY_MEM_ID
FROM REVIEW_REPLY
WHERE REVIEW_REPLY_NO = #{review_reply_no}

mapper.update:
UPDATE REVIEW_REPLY
    SET
    REVIEW_REPLY_CONTENT = #{review_reply_content}
WHERE
    REVIEW_REPLY_NO = #{review_reply_no}
