mapper.update:
UPDATE TRAVEL_TIP_BOARD


	   TRAVEL_TIP_TITLE		=	#{travel_tip_title},


   	   TRAVEL_TIP_CONTENT	=	#{travel_tip_content}


WHERE TRAVEL_TIP_NO =#{travel_tip_no}

mapper.insert:
SELECT TRAVEL_TIP_BOARD_SEQ.NEXTVAL FROM DUAL
INSERT INTO TRAVEL_TIP_BOARD ( TRAVEL_TIP_NO, TRAVEL_TIP_MEM_ID, TRAVEL_TIP_TITLE, TRAVEL_TIP_CONTENT, TRAVEL_TIP_DATE
) VALUES ( #{travel_tip_no}, #{travel_tip_mem_id}, #{travel_tip_title}, #{travel_tip_content}, SYSDATE
)

INSERT INTO BOARD_RCMD (
	BOARD_NO, RCMD_MEM_ID, BOARD_TYPE
) VALUES (
	#{travel_tip_no}, #{authMember}, 3
)

mapper.select:
SELECT NVL(COUNT(*), 0) FROM BOARD_RCMD
WHERE BOARD_NO = #{travel_tip_no}
AND RCMD_MEM_ID = #{authMember}
AND BOARD_TYPE = 3

mapper.delete:
DELETE FROM BOARD_RCMD
WHERE BOARD_NO = #{travel_tip_no}
AND BOARD_TYPE = 3

mapper.select:
SELECT NVL(COUNT(*), 0) FROM BOARD_REPORT
WHERE BOARD_NO = #{travel_tip_no}
AND REPORT_MEM_ID = #{authMember}
AND BOARD_TYPE = 3
